import React from 'react'
import Logopalkki from './Logopalkki'
import { useState, useEffect } from 'react'
import axios from 'axios'
import { useParams } from 'react-router-dom'



export default function RavintolaMuokkaus() {
    const [nimi, setNimi] = useState('');
    const [osoite, setOsoite] = useState('');
    const [tyyppi, setTyyppi] = useState('');
    const [aukioloajat, setAukioloajat] = useState('');
    const [hintataso, setHintataso] = useState('');

    const data = [nimi, osoite, tyyppi, aukioloajat, hintataso];
    console.log(data);

    const setData = (data) => {
        let { nimi, osoite, tyyppi, aukioloajat, hintataso } = data;
        setNimi(nimi);
        setOsoite(osoite);
        setTyyppi(tyyppi);
        setAukioloajat(aukioloajat);
        setHintataso(hintataso)      
}

  return (
      <div>
          <Logopalkki/>
    <div>Täälä muokataan ravintolan tietoja</div>
    <form >
            <label> Ravintolan nimi</label>
            <input placeholder=" Ravintolan nimi"/>
    </form> 
    <form >
            <label> Ravintolan Osoite</label>
            <input placeholder=" Ravintolan osoite"/>
    </form> 
    <form >
            <label> Ravintolan Tyyppi</label>
            <input placeholder=" Ravintolan tyyppi"/>
    </form> 
    <form >
            <label> Ravintolan aukioloajat</label>
            <input placeholder=" Ravintolan aukioloajat"/>
    </form> 
    <form >
            <label> Ravintolan hintataso</label>
            <input placeholder=" Ravintolanhintataso"/>
    </form> 
    <button onClick={() => setData(data)}>Päivitä</button>
    


    </div>
  )
}


