import React from 'react'
import { Link } from 'react-router-dom'
import { useState } from 'react'


export default function RavintolaLista(props) {
    const [ravintolat, setRavintolat] = useState(props.ravintolat)
    const [showAll, setShowAll] = useState(true)
    const [showAll2, setShowAll2] = useState(true)
    const [showAll3, setShowAll3] = useState(true)
    const [showAll4, setShowAll4] = useState(true)
    const [newNote2, setNewNote2] = useState('' )

    const handleNoteChange2 = (event) => {
        console.log(event.target.value)
        setNewNote2(event.target.value)
      }

    const ravintolatToShow = showAll
    ? ravintolat
    : ravintolat.filter(note => note.name.includes(newNote2))

    const ravintolatToShow2 = showAll2
    ? ravintolat
    : ravintolat.filter(note => note.cuisine_type.includes('Pizza'))

    const ravintolatToShow3 = showAll3
    ? ravintolat
    : ravintolat.filter(note => note.cuisine_type.includes('American'))

    const ravintolatToShow4 = showAll4
    ? ravintolat
    : ravintolat.filter(note => note.cuisine_type.includes('Mexican'))



  return (
    <div>
        <div className="filtteriPalkki">
         <div>
         <button onClick={() => setShowAll(!showAll)}>
          {showAll ? 'Näyttää kaikki ravintolat' : 'Hakee ravintolaa hakusanalla' }
         </button>

         <form>
            <input value = {newNote2}
            onChange = {handleNoteChange2}/>
            </form>
         </div>

        <button onClick={() => setShowAll2(!showAll2)}>
          {showAll2 ? 'Pizza' : 'Pizza' }
        </button>

        <button onClick={() => setShowAll3(!showAll3)}>
          {showAll3 ? 'American' : 'American' }
        </button>

        <button onClick={() => setShowAll4(!showAll4)}>
          {showAll4 ? 'Mexican' : 'Mexican' }
        </button>

        
        </div>
        { ravintolatToShow.map(ravintola =>
            <Link to={ ravintola.idd} key = {ravintola.idd}>
                <div className="ravintolaListaElementti">Ravintola:{ravintola.name} || Tyyli: {ravintola.cuisine_type} </div>
            </Link>
            )}

        { ravintolatToShow2.map(ravintola =>
            <Link to={ ravintola.idd} key = {ravintola.idd}>
                <div className="ravintolaListaElementti">Ravintola:{ravintola.name} || Tyyli: {ravintola.cuisine_type} </div>
            </Link>
            )}  



    </div>
  )
}

